mapper.select:
SELECT * FROM goods 
WHERE category_id = #{categoryId} 
	AND goods_title LIKE CONCAT('%', #{searchWord}, '%') 
	AND goods_exp >= now();

SELECT goods_id,goods_title,goods_exp,goods_date,goods_th_img,goods_avg_price,

(
SELECT COUNT(*) FROM goods 


	AND category_id=#{category_id}



	AND goods_title LIKE CONCAT('%', #{searchWord}, '%') 


) as totalCnt

FROM goods 



	AND category_id=#{category_id}



	AND goods_title LIKE CONCAT('%', #{searchWord}, '%') 



ORDER BY goods_id
